chrome.runtime.onMessage.addListener((t,n,r)=>{if(t.action==="fetchDefinition"){const o=t.word.toLowerCase(),i=`https://www.oxfordlearnersdictionaries.com/definition/english/${o}`;return fetch(i).then(e=>{if(!e.ok){if(e.status===404)return fetch(`https://www.oxfordlearnersdictionaries.com/definition/english/${o}_1`);throw new Error("Network response was not ok")}return e.text()}).then(e=>{r({success:!0,html:e,finalUrl:i})}).catch(e=>{r({success:!1,error:e.message})}),!0}});
